package OOPs;

public class BeanClass {
	
	public BeanClass(int id , String name, String color) {
		
		this.id = id;
		this.name = name;
		this.color = color;
		
		// TODO Auto-generated constructor stub
	}
	

	
	private int id ;
public int getId() {
		return id;
	}
	
	public String getName() {
		return name;
	}
	
	public String getColor() {
		return color;
	}
	


private String name;
	private String	 color;
	
	

}
